import numpy as npimport matplotlib.pyplot as pltfrom mpl_toolkits.mplot3d import Axes3Dimport osimport globfrom open3d import *import copydef draw_geometries_dark_background(geometries):    vis = Visualizer()    vis.create_window()    opt = vis.get_render_option()    opt.background_color = np.asarray([0, 0, 0])    for geometry in geometries:        vis.add_geometry(geometry)    vis.run()    vis.destroy_window()point_cloud_xyz_dir = "/home/kangning/Desktop/point_cloud_xyz/"point_cloud_transformed_dir = "/home/kangning/Desktop/point_cloud_transformed/"txt_file_names = os.listdir(point_cloud_xyz_dir)for index, txt_file_name in enumerate(txt_file_names):    sequence = txt_file_name.split(".txt")[0]    source_txt = np.loadtxt(point_cloud_xyz_dir+ sequence + ".txt")    transformed_txt = np.loadtxt(point_cloud_transformed_dir + sequence + ".txt")    print(source_txt.shape)    source_pcd = PointCloud()    source_pcd.points = Vector3dVector(source_txt)    transformed_pcd = PointCloud()    transformed_pcd.points = Vector3dVector(transformed_txt)    source_pcd.paint_uniform_color([255, 0, 0])      ####Red    transformed_pcd.paint_uniform_color([0, 0.651, 0.929])    ###Blue    #draw_geometries_dark_background([source_pcd,transformed_pcd])